import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './index.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [form, setForm] = useState({ title: '', description: '', section: '', date: new Date().toISOString().slice(0,10) });
  const [editIndex, setEditIndex] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date());

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.title || !form.section || !form.date) return;
    if (editIndex !== null) {
      const updatedTasks = tasks.map((task, idx) =>
        idx === editIndex ? { ...form } : task
      );
      setTasks(updatedTasks);
      setEditIndex(null);
    } else {
      setTasks([...tasks, { ...form }]);
    }
    setForm({ title: '', description: '', section: '', date: new Date().toISOString().slice(0,10) });
  };

  const handleDelete = (idx) => {
    setTasks(tasks.filter((_, i) => i !== idx));
    if (editIndex === idx) {
      setEditIndex(null);
      setForm({ title: '', description: '', section: '', date: new Date().toISOString().slice(0,10) });
    }
  };

  const handleEdit = (idx) => {
    setForm(tasks[idx]);
    setEditIndex(idx);
  };

  // Filtrar tareas por la fecha seleccionada
  const selectedDateString = selectedDate.toISOString().slice(0,10);
  const tasksForSelectedDate = tasks.filter(task => task.date === selectedDateString);

  // Resaltar días con tareas en el calendario
  const tileClassName = ({ date, view }) => {
    if (view === 'month') {
      const dateString = date.toISOString().slice(0,10);
      if (tasks.some(task => task.date === dateString)) {
        return 'calendar-has-task';
      }
    }
    return null;
  };

  return (
    <div className="app-container">
      <h1>Organizador <span style={{ color: "#1746a2" }}>Diario</span></h1>
      <form className="add-task-form" onSubmit={handleSubmit}>
        <div>
          <label className="add-task-label" htmlFor="title">Título</label>
          <input
            className="add-task-input"
            id="title"
            name="title"
            value={form.title}
            onChange={handleChange}
            placeholder="Ej: Revisión de proyecto"
            autoComplete="off"
          />
        </div>
        <div>
          <label className="add-task-label" htmlFor="description">Descripción</label>
          <textarea
            className="add-task-textarea"
            id="description"
            name="description"
            value={form.description}
            onChange={handleChange}
            placeholder="Describe la tarea..."
          />
        </div>
        <div>
          <label className="add-task-label" htmlFor="section">Sección</label>
          <input
            className="add-task-input"
            id="section"
            name="section"
            value={form.section}
            onChange={handleChange}
            placeholder="Ej: Ingeniería"
            autoComplete="off"
          />
        </div>
        <div>
          <label className="add-task-label" htmlFor="date">Fecha</label>
          <input
            className="add-task-input"
            id="date"
            name="date"
            type="date"
            value={form.date}
            onChange={handleChange}
          />
        </div>
        <div className="form-buttons" style={{ marginTop: 12 }}>
          <button type="submit">
            {editIndex !== null ? "Guardar Cambios" : "Agregar Tarea"}
          </button>
          {editIndex !== null && (
            <button
              type="button"
              className="cancel-btn"
              onClick={() => { setEditIndex(null); setForm({ title: '', description: '', section: '', date: new Date().toISOString().slice(0,10) }); }}
            >
              Cancelar
            </button>
          )}
        </div>
      </form>
      <div style={{ display: 'flex', gap: 32, flexWrap: 'wrap', justifyContent: 'center', alignItems: 'flex-start' }}>
        <div>
          <Calendar
            onChange={handleDateChange}
            value={selectedDate}
            locale="es-ES"
            tileClassName={tileClassName}
          />
        </div>
        <div style={{ minWidth: 260 }}>
          <h2 style={{ color: "#2563eb", marginBottom: 12, marginTop: 0 }}>
            Tareas para {selectedDate.toLocaleDateString()}
          </h2>
          <ul className="task-list">
            {tasksForSelectedDate.length === 0 && <p>No hay tareas para este día.</p>}
            {tasksForSelectedDate.map((task, idx) => (
              <li className="task-item" key={idx}>
                <div className="task-info">
                  <div className="task-title">{task.title}</div>
                  <div className="task-section">{task.section}</div>
                  <div className="task-desc">{task.description}</div>
                </div>
                <div className="task-actions">
                  <button onClick={() => handleEdit(tasks.findIndex(t => t === task))}>Editar</button>
                  <button className="delete-btn" onClick={() => handleDelete(tasks.findIndex(t => t === task))}>Eliminar</button>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
