import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [form, setForm] = useState({ title: '', description: '', section: '' });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.title || !form.section) return;
    setTasks([...tasks, { ...form }]);
    setForm({ title: '', description: '', section: '' });
  };

  return (
    <div style={{ padding: 40 }}>
      <h1 style={{ textAlign: 'center', color: '#2563eb' }}>Gestor de Tareas</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title:</label>
          <input name="title" value={form.title} onChange={handleChange} />
        </div>
        <div>
          <label>Description:</label>
          <textarea name="description" value={form.description} onChange={handleChange} />
        </div>
        <div>
          <label>Section:</label>
          <input name="section" value={form.section} onChange={handleChange} />
        </div>
        <button type="submit">Add Task</button>
      </form>
      <hr />
      <h2>Tareas</h2>
      {tasks.length === 0 && <p>No hay tareas.</p>}
      <ul>
        {tasks.map((task, idx) => (
          <li key={idx}>
            <strong>{task.title}</strong> ({task.section}): {task.description}
          </li>
        ))}
      </ul>
    </div>
  );
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
