import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [form, setForm] = useState({ title: '', description: '', section: '' });
  const [editIndex, setEditIndex] = useState(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.title || !form.section) return;
    if (editIndex !== null) {
      // Editar tarea existente
      const updatedTasks = tasks.map((task, idx) =>
        idx === editIndex ? { ...form } : task
      );
      setTasks(updatedTasks);
      setEditIndex(null);
    } else {
      // Agregar nueva tarea
      setTasks([...tasks, { ...form }]);
    }
    setForm({ title: '', description: '', section: '' });
  };

  const handleDelete = (idx) => {
    setTasks(tasks.filter((_, i) => i !== idx));
    // Si se está editando la tarea eliminada, limpiar el formulario
    if (editIndex === idx) {
      setEditIndex(null);
      setForm({ title: '', description: '', section: '' });
    }
  };

  const handleEdit = (idx) => {
    setForm(tasks[idx]);
    setEditIndex(idx);
  };

  return (
    <div style={{ padding: 40 }}>
      <h1 style={{ textAlign: 'center', color: '#2563eb' }}>Gestor de Tareas</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title:</label>
          <input name="title" value={form.title} onChange={handleChange} />
        </div>
        <div>
          <label>Description:</label>
          <textarea name="description" value={form.description} onChange={handleChange} />
        </div>
        <div>
          <label>Section:</label>
          <input name="section" value={form.section} onChange={handleChange} />
        </div>
        <button type="submit">{editIndex !== null ? "Guardar Cambios" : "Add Task"}</button>
        {editIndex !== null && (
          <button type="button" onClick={() => { setEditIndex(null); setForm({ title: '', description: '', section: '' }); }}>
            Cancelar
          </button>
        )}
      </form>
      <hr />
      <h2>Tareas</h2>
      {tasks.length === 0 && <p>No hay tareas.</p>}
      <ul>
        {tasks.map((task, idx) => (
          <li key={idx}>
            <strong>{task.title}</strong> ({task.section}): {task.description}
            <button style={{ marginLeft: 10 }} onClick={() => handleEdit(idx)}>Editar</button>
            <button style={{ marginLeft: 5 }} onClick={() => handleDelete(idx)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
