import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [form, setForm] = useState({ title: '', description: '', section: '' });
  const [editIndex, setEditIndex] = useState(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.title || !form.section) return;
    if (editIndex !== null) {
      const updatedTasks = tasks.map((task, idx) =>
        idx === editIndex ? { ...form } : task
      );
      setTasks(updatedTasks);
      setEditIndex(null);
    } else {
      setTasks([...tasks, { ...form }]);
    }
    setForm({ title: '', description: '', section: '' });
  };

  const handleDelete = (idx) => {
    setTasks(tasks.filter((_, i) => i !== idx));
    if (editIndex === idx) {
      setEditIndex(null);
      setForm({ title: '', description: '', section: '' });
    }
  };

  const handleEdit = (idx) => {
    setForm(tasks[idx]);
    setEditIndex(idx);
  };

  return (
    <div className="app-container">
      <h1>Gestor de <span style={{ color: "#1746a2" }}>Tareas</span></h1>
      <form className="add-task-form" onSubmit={handleSubmit}>
        <div>
          <label className="add-task-label" htmlFor="title">Título</label>
          <input
            className="add-task-input"
            id="title"
            name="title"
            value={form.title}
            onChange={handleChange}
            placeholder="Ej: Revisión de proyecto"
            autoComplete="off"
          />
        </div>
        <div>
          <label className="add-task-label" htmlFor="description">Descripción</label>
          <textarea
            className="add-task-textarea"
            id="description"
            name="description"
            value={form.description}
            onChange={handleChange}
            placeholder="Describe la tarea..."
          />
        </div>
        <div>
          <label className="add-task-label" htmlFor="section">Sección</label>
          <input
            className="add-task-input"
            id="section"
            name="section"
            value={form.section}
            onChange={handleChange}
            placeholder="Ej: Ingeniería"
            autoComplete="off"
          />
        </div>
        <div className="form-buttons" style={{ marginTop: 12 }}>
          <button type="submit">
            {editIndex !== null ? "Guardar Cambios" : "Agregar Tarea"}
          </button>
          {editIndex !== null && (
            <button
              type="button"
              className="cancel-btn"
              onClick={() => { setEditIndex(null); setForm({ title: '', description: '', section: '' }); }}
            >
              Cancelar
            </button>
          )}
        </div>
      </form>
      <h2 style={{ color: "#2563eb", marginBottom: 12 }}>Tareas</h2>
      <ul className="task-list">
        {tasks.length === 0 && <p>No hay tareas.</p>}
        {tasks.map((task, idx) => (
          <li className="task-item" key={idx}>
            <div className="task-info">
              <div className="task-title">{task.title}</div>
              <div className="task-section">{task.section}</div>
              <div className="task-desc">{task.description}</div>
            </div>
            <div className="task-actions">
              <button onClick={() => handleEdit(idx)}>Editar</button>
              <button className="delete-btn" onClick={() => handleDelete(idx)}>Eliminar</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
